words = []

for i in answer:
    word=list(i)
    shuffle(word)
    words.append(word)
    
num=random.randint(0,len(words))
    
def initial():
    global words,num
    lb1.configure(text=words[num])
    
def Reset():
    global words,num,answer
    num=random.randint(0,len(words))
    lb1.configure(text=words[num])
    e1.delete(0,END)
    

def ans_check():
    global words,num,answer
    user_input=e1.get()
    if user_input==answer[num]:
        messagebox.showinfo("Success","Congratulations this is correct :)")
        Reset()
    else:
        messagebox.showinfo("Error","Sorry this is incorrect :(")
        e1.delete(0,END)
        Reset()
        
    
root = tkinter.Tk()
root.title("Guess The Word!")
root.geometry("500x500")

lb1 = Label(root,font="times 20")
lb1.pack(pady=30,ipady=10,ipadx=10)

answer12=StringVar()
e1 = Entry(root,textvariable=answer)
e1.pack(ipady=5,ipadx=5)

button1 = Button(root,text="Check",width=20,command=ans_check)
button1.pack(pady=40)

button2 = Button(root,text="Reset",width=20,command=Reset)
button2.pack()

initial()

root.mainloop()